 stateDirection: (ScrollDirection) -> Unit = {}
) {
 if (coffeeList.isEmpty()) return

 // Initialize pager with the clicked coffee index, ensuring it's within bounds
 val initialPage = coffeeClicked.coerceIn(0, coffeeList.size - 1)
