@Composable
fun SplashScreen(modifier: Modifier = Modifier) {
 var animationStarted by remember { mutableStateOf(false) }

 val scale by animateFloatAsState(
 targetValue = if (animationStarted) 1f else 2.2f,
 animationSpec = tween(
 durationMillis = 1000 // La duración de la animación
 ),
 label = "scaleAnimation" // Etiqueta para depuración
 )

 var sizeIcon = remember { 30.dp }
 val alpha = remember { mutableFloatStateOf(0f) }
 LaunchedEffect(Unit) {
 animate(
 initialValue = 0f,
 targetValue = 1f,
 animationSpec = tween(1300)
 ) { value, _ ->
 alpha.floatValue = value
 }
 }
 LaunchedEffect(alpha.floatValue) {
 if (alpha.floatValue == 1f) {
 sizeIcon = 50.dp
 animationStarted = true
 }
 }

 Box(
 modifier = Modifier
 .fillMaxSize()
 .background(Color(0xFFe8005a)),
 contentAlignment = Alignment.Center
 ) {

 Icon(
 painterResource(com.alonso.designsystem.R.drawable.ic_star),
 contentDescription = null,
 modifier = Modifier
 .align(Alignment.Center)
 .size(sizeIcon)
 .scale(scale)
 .graphicsLayer(
 scaleX = 3.0f,
 scaleY = 3.0f,
 alpha = alpha.floatValue
 ),
 tint = Color.White
 )
 // Pulsating()

 }
}

