@Composable
fun SplashScreen(modifier: Modifier = Modifier) {
 var animationStarted by remember { mutableStateOf(false) }

 // Unified alpha animation that controls both fade-in and scale timing
 val alpha by animateFloatAsState(
 targetValue = if (animationStarted) 1f else 0f,
 animationSpec = tween(durationMillis = 1300),
 label = "alphaAnimation"
 )

 // Scale animation that starts after alpha reaches 1f
 val scale by animateFloatAsState(
 targetValue = if (animationStarted && alpha >= 0.9f) 1f else 3f,
 animationSpec = tween(durationMillis = 1000),
 label = "scaleAnimation"
 )

 // Start animation on composition
 LaunchedEffect(Unit) {
 delay(200) // Small delay for better visual effect
 animationStarted = true
 }

 Box(
 modifier = modifier
 .fillMaxSize()
 .background(Color(0xFFe8005a)),
 contentAlignment = Alignment.Center
 ) {
 Icon(
 painter = painterResource(com.alonso.designsystem.R.drawable.ic_star),
 contentDescription = "App Logo",
 modifier = Modifier
 .size(50.dp)
 .graphicsLayer(
 scaleX = scale,
 scaleY = scale,
 alpha = alpha
 ),
 tint = Color.White
 )
 }
}

